import React, { useState } from 'react';

import { WorkDay } from '../../models';

import Button from '@material-ui/core/Button';
import TextField from '@material-ui/core/TextField';
import Dialog from '@material-ui/core/Dialog';
import DialogActions from '@material-ui/core/DialogActions';
import DialogContent from '@material-ui/core/DialogContent';
import DialogContentText from '@material-ui/core/DialogContentText';
import DialogTitle from '@material-ui/core/DialogTitle';
import List from '@material-ui/core/List';
import ListItem from '@material-ui/core/ListItem';
import ListItemText from '@material-ui/core/ListItemText';

interface Props {
	confirmed: (day: WorkDay) => void;
	canceled: () => void;
	workDays: WorkDay[];
}


export const ChangeWorkDayForm = (props: Props) => {
	const {confirmed, canceled} = props;
	const [daySelectorOpened, setDaySelectorOpened] = useState({day: 0, amount: 0});
	const [day, setWorkDay] = useState({day: 0, amount: 0});

	const onAmountChange = (e: React.ChangeEvent<HTMLInputElement>) => {
		setWorkDay({
			day: 0,
			amount: Number(e?.target.value) || 0
		});
	}

	const onCanceled = () => {
		canceled();
	}

	const onConfirmed = () => {
		confirmed(day);
	}

	return (
		<>
			<DialogTitle>Изменить смену</DialogTitle>
	        <DialogContent>
		        <List dense={dense}>
	              {workDays.map((day) => <ListItem> </ListItem>)}
	            </List>

	          <TextField
	            margin="dense"
	            label="Количество"
	            type="number"
	            required
	            fullWidth
	            onChange={onAmountChange}
	          />
	        </DialogContent>
	        <DialogActions>
	          <Button onClick={onCanceled} color="primary">
	            Отмена
	          </Button>
	          <Button onClick={onConfirmed} color="primary" disabled={day.amount < 1}>
	            Добавить
	          </Button>
	        </DialogActions>
		</>
	)
}
