import React, { useState } from 'react';

import { WorkMonth } from '../../models';

import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableContainer from '@material-ui/core/TableContainer';
import TableHead from '@material-ui/core/TableHead';
import TableRow from '@material-ui/core/TableRow';
import Paper from '@material-ui/core/Paper';

interface Props {
	month: WorkMonth;
}

export const ProductionTable = (props: Props) => {
	const {month} = props;
	return (
		<TableContainer component={Paper}>
	      <Table className="production-table__table" stickyHeader>
	        <TableHead >
	          <TableRow>
	            <TableCell>План</TableCell>
	            <TableCell align="right">Остаток</TableCell>
	          </TableRow>
	        </TableHead>
	        <TableBody>
	          {month.workDays.map((workDay) => (
	            <TableRow key={workDay.day}>
	              <TableCell component="th" scope="row">
	                {workDay.day}
	              </TableCell>
	              <TableCell align="right">{workDay.amount}</TableCell>
	            </TableRow>
	          ))}
	        </TableBody>
	      </Table>
	    </TableContainer>
	)
}


