import React, { useState } from 'react';

import { InputField, Button, BtnTypes } from '../';
import { WorkMonth, Products, WorkDay } from '../../models';

import './NewMonthForm.css';

interface Props {
	confirmed: (m: WorkMonth) => unknown;
  canceled: () => unknown;
  product: Products;
}

export const NewMonthForm = (props: Props) => {
  const {confirmed, canceled, product} = props;
  const [days, setDays] = useState(0);
  const [plan, setPlan] = useState(0);

  const onDaysChange = (days: string) => {
    setDays(Number(days));
  }

  const onPlanChange = (plan: string) => {
    setPlan(Number(plan));
  }

  const onConfirmed = () => {
    debugger
    const workDays: WorkDay[] = [];
    for (let i = 0; i < days; ++i) {
      workDays.push({
        amount: 0,
        day: i
      })
    }

    const m: WorkMonth = {
      product,
      days,
      plan,
      ppr: 0,
      workDays,
    }

    confirmed(m);
  }

  return (
    <form className="form">
      <section className="form__fields">
        <div className="form__field">
           <label>Количество смен</label>
          <InputField type={'text'} value={String(days)} changed={onDaysChange} />         
        </div>

        <div className="form__field">
          <label>План</label>
          <InputField type={'text'} value={String(plan)} changed={onPlanChange} />           
        </div>
      </section>


      <section className="form__controls">
        <Button text={'Отмена'} type={BtnTypes.warn} clicked={canceled}/>
        <Button text={'Создать'} type={BtnTypes.primary} clicked={onConfirmed}/>
      </section>
    </form>
  )
}
