import React from 'react';

import './Button.css';

export enum BtnTypes {
  primary = 'primary',
  warn = 'warn',
  default = 'default'
}

interface Props {
  clicked?: () => unknown;
  text: string;
  type?: BtnTypes;
}

export const Button = (props: Props) => {
  const { clicked, text, type = BtnTypes.default } = props;

  const getStyle = (type: BtnTypes) => {
    let style = 'button ';
    switch (type) {
      case BtnTypes.primary:
        return style + 'button-primary';
        break;

      case BtnTypes.warn: {
        return style + 'button-warn';
      }

      default:
        return style + 'button-default';
        break;
    }
  }

	return (
    <button className={getStyle(type)} 
            data-theme="light"
            onClick={clicked}>
      {text}
    </button>
  )
}
