import React, { useState, useEffect, useContext } from 'react';
import { Link } from 'react-router-dom';

import { Button, BtnTypes, Menu, MenuItem, ErrorBoundary, ProductionTable } from '../components';
import { DBServiceContext } from '../App';

import { data } from '../models';

import './ProductionPage.css';

export const ProductionPage = () => {
  const [month, setMonth] = useState({});
  const [loading, setLoading] = useState(true);
  const dbService = useContext(DBServiceContext);
  const getMonth = async () => {
    const m = await dbService.getMonth();
    console.log(m);
    setMonth(m);
  }

  useEffect(() => {
    // const d = getMonth();
    // console.log(d)
    // setTimeout(() => {
    //   setMonth(data);
    //   setLoading(false);
    //   console.log(data)
    // }, 1000)
    getMonth();
    setLoading(false);
  }, [])


  const itemClicked = (t: string) => {
    console.log(t)
  }

  const itemSelected = (i: string) => {
    console.log(i)
  }

  const items = [  
            {
              text: 'Новый месяц',
            },
            {
              text: 'Новая смена',
            },
            {
              text: 'Изменить план',
            },
            {
              text: 'Изменить количество смен'
            },
          ];

	return (
    <ErrorBoundary>
    {
      loading 
      ? <h1>Loading...</h1> 
      : <section className="production-page__container">
          <nav className="navigation__container">
            <li className="navigation-item">
              <Link to="/"><Button text={'Назад'} type={BtnTypes.warn}/></Link>
            </li>
            <li className="navigation-item">
              <Menu items={items} selected={itemSelected} />
            </li>
          </nav>

          <ProductionTable month={data}/>
        </section>
    }

    </ErrorBoundary>
  );
}
