import React from 'react';

import './Button.css';

enum BtnTypes {
  primary = 'primary',
  warn = 'warn',
}

interface Props {
  clicked?: () => unknown;
  text: string;
  type: string;
}

export const Button = (props: Props) => {
  const { clicked, text, type } = props;
  const getStyle = (type: BtnTypes) => {
    let style = 'button ';
    switch (type) {
      case BtnTypes.primary:
        return style + '';
        break;

      default:
        // code...
        break;
    }
  }

	return (
    <button className="button" 
            data-theme="light"
            onClick={clicked}>
      {text}
    </button>
  )
}
