import React, { useState, useEffect, useContext } from 'react';
import { useParams } from 'react-router-dom';


import { InputField, Button, BtnTypes } from '../';

import './ProductionTable.css';

import { WorkMonth, WorkDay, Products } from '../../models';

import { DBServiceContext } from '../../App';

interface Props {
  month: WorkMonth;
}

const buffer = (f: () => unknown, time: number) => {
  setTimeout(f , time);
}

const getKForToilets = (avg: number) => {
  if (avg > 22700 && avg < 26000) {
    return 1.1;
  }

  if (avg > 26000 && avg < 27000) {
    return 1.2;
  }

  if (avg > 27000) {
    return 1.25;
  }

  return 1.0;
}

const getKForNapkins = (avg: number) => {
  if (avg > 2100 && avg < 2401) {
    return 1.1;
  }
  if (avg > 2400 && avg < 2501) {
    return 1.2;
  }

  if (avg > 2500) {
    return 1.25;
  }

  return 1.0;
}

const getFinishedWorkDays = (workDays: WorkDay[]): number => {
  if (!workDays) return 0;
  let days = 0;
  workDays.forEach((d: WorkDay) => {
    if (d.amount > 0)
      ++days;
  });

  return days;
}

export const ProductionTable = (props: Props) => {
  const [remainToDo, setRemainToDo] = useState(0);
  const [avg, setAvg] = useState(0);
  const [avgWithPpr, setAvgWithPpr] = useState(0);
  const [needsToDo, setNeedsToDo] = useState(0);
  const [k, setK] = useState(0);

  const dbService = useContext(DBServiceContext);
  const {product} = useParams();
  const [month, setMonth] = useState({
    product,
    ppr: 0,
    days: 0,
    plan: 0,
    workDays: []
  } as WorkMonth);

  useEffect(() => {
    const m = props.month;
    updateAllData(m);
  }, [props.month])

  const onWorkDayChange = (day: number) => {
    return async (amount: string) => {
      const m = {...month};
      m.workDays[day].amount = + amount || 0;

      setMonth(m);
      remainToDoChanged(m);
      avgChanged(m);
      avgWithPprChanged(m);
      onNeedsToDoChange(m);
      onKChange(m);
      await dbService.updateDay(product, day, + amount);
    }
  }

  const remainToDoChanged = (m: WorkMonth) => {
    const res = m.plan - m.workDays?.reduce((acc, d) => acc + d.amount, 0) || 0;
    setRemainToDo(res > 0 ? res : 0);
  }

  const onPlanChange = async (p: string) => {
    await dbService.updatePlan(product, + p);
    const m = {...month};
    m.plan = + p;
    setMonth(m);
    remainToDoChanged(m);
  }

  const avgChanged = (m: WorkMonth) => {
    let res = m.workDays?.reduce((acc, d) => acc + d.amount, 0) || 0;
    const finished = getFinishedWorkDays(m.workDays);
    res = Math.round(res / (finished || 1));
    setAvg(res);
  }

  const avgWithPprChanged = (m: WorkMonth) => {
    const finished = getFinishedWorkDays(m.workDays);
    const avg = Math.round(
      m.workDays?.reduce((acc, d) => acc + d.amount, 0) || 0 / ((finished - m.ppr / 10) || 1),
    );
    setAvgWithPpr(avg);
  }

  const onPprChange = async (p: string) => {
    await dbService.updatePpr(product, + p);
    const m = {...month};
    m.ppr = + p;
    setMonth(m);
    avgWithPprChanged(m);
    onKChange(m);
  }

  const onNeedsToDoChange = (m: WorkMonth) => {
    const remainToDo = m.plan - m.workDays?.reduce((acc, d) => acc + d.amount, 0) || 0;
    const finished = getFinishedWorkDays(m.workDays);
    const res = Math.round(remainToDo / ((m.days - finished) || 1));
    setNeedsToDo(res > 0 ? res : 0);
  }

  const onKChange = (m: WorkMonth) => {
    const finished = getFinishedWorkDays(m.workDays);
    const avg = Math.round(m.workDays?.reduce((acc, d) => acc + d.amount, 0) || 0 / ((finished - m.ppr / 10) || 1));
    if (m.product === Products.napkins)
      setK(getKForNapkins(avg));

    if (m.product === Products.toilets)
      setK(getKForToilets(avg));
  }

  const onDaysChange = async (days: String) => {
    await dbService.updateDays(product, + days);
    const m = {...month};
    m.days = + days;
    m.workDays = m.workDays.slice(0, + days);
    updateAllData(m);
  }

  const onAddNewDay = async () => {
    const m = {...month};
    const day = m.workDays?.length || 0;
    m.workDays?.push({
      day,
      amount: 0
    });
    await dbService.addWorkDay(product, day, 0);
    updateAllData(m);
  }

  const onDeleteDay = async () => {
    const m = {...month};
    const day = m.workDays.length;
    m.workDays.pop();
    await dbService.deleteWorkDay(product, day);
    updateAllData(m);
  }

  const updateAllData = (m: WorkMonth) => {
    setMonth(m);
    remainToDoChanged(m);
    avgChanged(m);
    avgWithPprChanged(m);
    onNeedsToDoChange(m);
    onKChange(m);
  }

	return (
    <table className="production-table" data-theme="light">
      <thead className="production-table__thead">
        <tr className="production-table__tr">
          <th className="production-table__td">План</th>
          <th className="production-table__td"><Button text={"+"} type={BtnTypes.primary} clicked={onAddNewDay} /></th>
          <th className="production-table__td"><Button text={"-"} type={BtnTypes.warn} clicked={onDeleteDay} /></th>
          <th className="production-table__td">Остаток</th>
        </tr>
        <tr className="production-table__tr">
          <td className="production-table__td">
            <InputField type={'text'} value={String(month.plan || 0)} changed={onPlanChange}/>
          </td>
          <td className="production-table__td">
            <InputField type={'text'} value={String(remainToDo)} disabled={true}/>
          </td>
        </tr>
        <tr className="production-table__tr">
          <th className="production-table__td">Коэффициент</th>
          <th className="production-table__td">Среднее в смену</th>
        </tr>
        <tr className="production-table__tr">
          <td className="production-table__td">
            <InputField type={'text'} value={String(k)} disabled={true}/>
          </td>
          <td className="production-table__td">
            <InputField type={'text'} value={String(avg)} disabled={true}/>
          </td>
        </tr>
        <tr className="production-table__tr">
          <th className="production-table__td">Часы ППР</th>
          <th className="production-table__td">Среднее с ППР</th>
        </tr>
        <tr className="production-table__tr">
          <td className="production-table__td">
            <InputField type={'text'} value={String(month.ppr || 0)} changed={onPprChange}/>
          </td>
          <td className="production-table__td">
            <InputField type={'text'} value={String(avgWithPpr)} disabled={true}/>
          </td>
        </tr>
        <tr className="production-table__tr">
          <th className="production-table__td">Смена</th>
          <th className="production-table__td">Количество</th>
        </tr>
      </thead>
      <tbody className="production-table_tbody">
          { month.workDays?.map((workDay, i) => {
              return (
                <tr key={i} className="production-table__tr">
                 <td className="production-table__td">
                    <InputField type={'text'} value={String(workDay.day + 1)} disabled={true}/>
                 </td>
                 <td className="production-table__td">
                    <InputField type={'text'} value={String(workDay.amount)} changed={onWorkDayChange(workDay.day)}/>
                 </td>
                </tr>
              )
            })
          }

      </tbody>
      <tfoot className="production-table__tfoot">
        <tr className="production-table__tr">
          <th className="production-table__td">Для выполнения плана нужно делать по:</th>
          <th className="production-table__td"><InputField type={'text'} value={String(needsToDo)} disabled={true}/></th>
        </tr>
      </tfoot>
    </table>
  )
}
