import React, { useState, useRef } from 'react';
import { Link } from 'react-router-dom';

import { Button, BtnTypes, Menu, MenuItem } from '../components';

import './ProductionPage.css';

export const ProductionPage = () => {
  const [ menuOpened, toggleMenu ] = useState(false);
  const itemClicked = (t: string) => {
    console.log(t)
    toggleMenu(false);
  }
  const [r] = useRef();

  const items = [  
            {
              text: 'Новый месяц',
              clicked: itemClicked
            },
            {
              text: 'Новая смена',
              clicked: itemClicked
            },
            {
              text: 'Изменить план',
              clicked: itemClicked
            },
            {
              text: 'Изменить количество смен',
              clicked: itemClicked
            },
          ];
  const btn = <Button text={'Управление'} ref={r} type={BtnTypes.primary} clicked={() => toggleMenu(!menuOpened)}/>
	return (
    <section className="production-page__container">
      <nav className="navigation__container">
        <li className="navigation-item">
          <Link to="/"><Button text={'Назад'} type={BtnTypes.warn}/></Link>
        </li>
        <li className="navigation-item" id="connector">
          { btn }
        </li>
        { menuOpened && <Menu items={items} connector={btn} />}
      </nav>
    </section>
  );
}
