import { WorkMonth, Products } from '../models';

export class DBService {
	private readonly url = 'http://localhost:4000/api';

	public async getMonth(p: Products): Promise<WorkMonth> {
		try {
			debugger
			const res = await fetch(`${this.url}/month/${p}`);

		    if (!res.ok) {
		      throw new Error(`Could not fetch ${this.url}/month/${p}` +
		        `, received ${res.status}`)
		    }

		    const result = await res.json()
		    return result;

		} catch (e) {
			console.log(e.message);
			return await {} as WorkMonth;
		}

	}

	public async updateWorkDay(p: Products, day: number, amount: number) {
		console.log(amount)
	}

	public async updatePlan(p: Products, plan: number) {
		console.log(plan)
	}

	public async updatePpr(p: Products, ppr: number) {
		const res = await fetch(`${this.url}/ppr/${p}`, {
			method: 'PUT',
		    headers: {
		      'Content-Type': 'application/json'
		    },
		    body: JSON.stringify(ppr)
		})
	}
}
