import React, { useState } from 'react';
import { InputField, Button, BtnTypes, Modal } from '../';

interface Props {
	value: string;
	changed: (v: string) => unknown;
	editConfirmed?: (v: string) => unknown;
	deleteConfirmed?: (v: string) => unknown;
}

export const TableDataCell = (props: Props) => {
	const [edit, setEdit] = useState(false);
	const [del, setDelete] = useState(false);
	const {value, changed, editConfirmed, deleteConfirmed} = props;

	const onValChange = (v: string) => {
		changed(v);
	}

	const onEditBtnClicked = () => {
		setEdit(!edit)
	}

	const onEditMenuClosed = () => {
		setEdit(!edit)
	}

	const onDeleteBtnClicked = () => {
		setDelete(true);
	}

	return (
		<>
		<td className="table__td">
		<InputField type={'text'} value={value} changed={onValChange}/>
		<Button text={'e'} clicked={onEditBtnClicked}/>
		<Button text={'del'} clicked={onDeleteBtnClicked}/>
		</td>
		{	<>
			edit && <Modal 
			opened={edit} 
			closed={onEditMenuClosed}
			children={
				<form className="form">
				<section className="form__fields">
				<div className="form__field">
				<label>Количество смен</label>
				<InputField type={'text'} value={String(days)} changed={onDaysChange} />         
				</div>

				<div className="form__field">
				<label>План</label>
				<InputField type={'text'} value={String(plan)} changed={onPlanChange} />           
				</div>
				</section>


				<section className="form__controls">
				<Button text={'Отмена'} type={BtnTypes.warn} clicked={canceled}/>
				<Button text={'Создать'} type={BtnTypes.primary} clicked={onConfirmed}/>
				</section>
				</form>
			}
			/> 

			del && <Modal 
			opened={edit} 
			closed={onEditMenuClosed}
			children={
				<form className="form">
				<section className="form__controls">
				<label>Удалить смену?</label>
				<Button text={'Отмена'} type={BtnTypes.warn} clicked={canceled}/>
				<Button text={'Удалить'} type={BtnTypes.primary} clicked={onConfirmed}/>
				</section>
				</form>
			}
			/> 
			</>
		}
		</>
		)
}
