import React, { useState, useRef } from 'react';
import { InputField, Button, BtnTypes, Modal } from '../';

interface Props {
	value: string;
	valChanged: (v: string) => unknown
}

export const TableDataCell = (props: Props) => {
	const [edit, setEdit] = useState(false);
	const {value, valChanged} = props;
	const td = useRef(null);

	const onValChange = (v: string) => {
		valChanged(v);
	}

	const onEditBtnClicked = () => {
		setEdit(!edit)
	}

	return (
		<>
			<td className="table__td" ref={td}>
				<InputField type={'text'} value={value} changed={onValChange}/>
				<Button text={'e'} clicked={onEditBtnClicked}/>
			</td>
			{
				edit && <Modal > </Modal>
			}
		</>
	)
}
