import React, { useRef } from 'react';

import './InputField.css';

interface Props {
  type: 'text' | 'number' | 'email';
  value: string;
  required?: boolean;
  disabled?: boolean;
  changed: (v: string) => string;
}

export const InputField = (props: Props) => {
  const { type, value, required, changed } = props;
  const inputEl = useRef<HTMLInputElement>(null);
  const valChanged = () => {
    console.dir(inputEl)
    changed('53');
  }

	return <input className="input"
                type={type} 
                value={value} 
                required={required}
                onChange={valChanged}
                ref={inputEl}
                data-theme="dark"/>
}
