import React, { useState, useEffect, useContext } from 'react';
import { Link, useParams } from 'react-router-dom';

import { 
  Button, 
  BtnTypes, 
  Menu, 
  MenuItem, 
  ErrorBoundary, 
  ProductionTable,
  Spinner,
  Modal
} from '../components';

import { DBServiceContext } from '../App';
import { WorkMonth, Products } from '../models';

import napkinsImg from '../assets/napkins.webp';
import toiletsImg from '../assets/toilets.webp';
import './ProductionPage.css';


export const ProductionPage = () => {
  const [month, setMonth] = useState({} as WorkMonth);
  const [loading, setLoading] = useState(true);
  const [modalOpened, setModalOpened] = useState(false);


  const {product} = useParams();
  const dbService = useContext(DBServiceContext);

  useEffect(() => {
    setTimeout(() => {
      const m = dbService.getMonth();
      setMonth(m);
      setLoading(false);
    }, 1000)
  }, [])

  useEffect(() => {
    window.addEventListener('mousedown', onWindowClick);
    return () => window.removeEventListener('mousedown', onWindowClick);
  }, []);

  const onWindowClick = (e: Event) => {
    console.log(e)
    setModalOpened(false);
  }

  const itemClicked = (t: string) => {
    console.log(t)
  }

  const itemSelected = (i: string) => {
    console.log(i)
  }

  const onModalToggle = () => {
    setModalOpened(!modalOpened);
    console.log(modalOpened)
  }

  const imgSrc = product === Products.toilets ? toiletsImg : napkinsImg;
  const spinner = () => {
    return (
      <div className="spinner-container">
        <Spinner imgSrc={imgSrc}/> 
      </div>
    )
  }
 	return (
    <ErrorBoundary>
    {
      loading 
      ? spinner()
      : <section className="production-page__container" data-theme="light">
          <nav className="navigation__container">
            <li className="navigation-item">
              <Link to="/"><Button text={'Назад'} type={BtnTypes.warn}/></Link>
            </li>
            <li className="navigation-item">
              <Button text={'Новый месяц'} clicked={onModalToggle}/>
            </li>
          </nav>

          <ProductionTable month={month}/>
        </section>
    }

      <Modal opened={modalOpened} children={<h1>MODAL CONTENT</h1>} />
    </ErrorBoundary>
  );
}
