import React, { useState } from 'react';
import { InputField, Button, BtnTypes, Modal } from '../';

interface Props {
	value: string;
	changed: (v: string) => unknown;
	editConfirmed?: (v: string) => unknown;
	deleteConfirmed?: () => unknown;
}

export const TableDataCell = (props: Props) => {
	const [edit, setEdit] = useState(false);
	const [del, setDelete] = useState(false);
	const {value, changed, editConfirmed, deleteConfirmed} = props;

	const onValChange = (v: string) => {
		changed(v);
	}

	const onEditBtnClicked = () => {
		setEdit(!edit)
	}

	const onEditMenuClosed = () => {
		setEdit(!edit)
	}

	const onDeleteBtnClicked = () => {
		setDelete(true);
	}

	const onDeleteConfirmed = () => {
		if (deleteConfirmed)
			deleteConfirmed();

		setDelete(false);
	}

	const onEditConfirmed = () => {
		if (editConfirmed)
			editConfirmed(value);

		setEdit(false)
	}

	const onDeleteCanceled = () => {
		setDelete(false);
	}

	const onEditCanceled = () => {
		setEdit(false)
	}

	return (
		<>
		<td className="table__td">
		<InputField type={'text'} value={value} changed={onValChange}/>
		<Button text={'e'} clicked={onEditBtnClicked}/>
		<Button text={'del'} clicked={onDeleteBtnClicked}/>
		</td>
		{	<>
			edit && <Modal 
			opened={edit} 
			closed={onEditMenuClosed}
			children={
				<form className="form">
				<section className="form__controls">
				<label>Количество смен</label>
				<Button text={'Отмена'} type={BtnTypes.warn} clicked={onDeleteCanceled}/>
				<Button text={'Создать'} type={BtnTypes.primary} clicked={onEditConfirmed}/>
				</section>
				</form>
			}
			/> 

			del && <Modal 
			opened={edit} 
			closed={onEditMenuClosed}
			children={
				<form className="form">
				<section className="form__controls">
				<label>Удалить смену?</label>
				<Button text={'Отмена'} type={BtnTypes.warn} clicked={onDeleteCanceled}/>
				<Button text={'Удалить'} type={BtnTypes.primary} clicked={onDeleteConfirmed}/>
				</section>
				</form>
			}
			/> 
			</>
		}
		</>
		)
}
