import React, { useState, useEffect, useContext } from 'react';
import { Link, useParams } from 'react-router-dom';

import { 
  // Button, 
  BtnTypes, 
  Menu, 
  MenuItem, 
  ErrorBoundary, 
  ProductionTable,
  Spinner,
  Modal,
  NewMonthForm,
  ChangeMonthForm
} from '../components';

import { Button } from '@material-ui/core/Button';

import { DBServiceContext } from '../App';
import { WorkMonth, Products } from '../models';

import napkinsImg from '../assets/napkins.webp';
import toiletsImg from '../assets/toilets.webp';
import './ProductionPage.css';


export const ProductionPage = () => {
  const [month, setMonth] = useState({} as WorkMonth);
  const [loading, setLoading] = useState(true);
  const [modalOpened, setModalOpened] = useState(false);
  const [changeMonthModalOpened, setChangeMonthModalOpened] = useState(false);


  const {product} = useParams();
  const dbService = useContext(DBServiceContext);

  useEffect(() => {
      (async () => {
        const m = await dbService.getMonth(product);
        setMonth(m);
        setLoading(false);
      })();
  }, [])

  const onModalToggle = () => {
    setModalOpened(!modalOpened);
  }

  const onCreateNewMonthCanceled = () => {
    setModalOpened(false);
  }

  const onChangeMonthModalToggle = () => {
    setChangeMonthModalOpened(!changeMonthModalOpened);
  }

  const onChangeMonthCanceled = () => {
    setChangeMonthModalOpened(false);
  }

  const onChangeMonthConfirmed = (plan: number, days: number, ppr: number) => {
    (async () => {
      await dbService.updatePlan(product, plan);
      await dbService.updateDays(product, days);
      await dbService.updatePpr(product, ppr);
      setChangeMonthModalOpened(false);
    })();
  }

  const onCreateNewMonthConfirmed = async (month: WorkMonth) => {
    await dbService.addNewMonth(month.product, month.plan, month.days, 0);
    setModalOpened(false);
  }

  const imgSrc = product === Products.toilets ? toiletsImg : napkinsImg;
  const spinner = () => {
    return (
      <div className="spinner-container">
        <Spinner imgSrc={imgSrc}/> 
      </div>
    )
  }
 	return (
    <ErrorBoundary>
    {
      loading 
      ? spinner()
      : <section className="production-page__container" data-theme="light">
          <nav className="navigation__container">
            <Button variant="contained" color="primary" href="#contained-buttons">
              Link
            </Button>
            <li className="navigation-item">
              <Button text={'Новый месяц'} clicked={onModalToggle}/>
            </li>
            <li className="navigation-item">
              <Button text={'Изменить месяц'} clicked={onChangeMonthModalToggle}/>
            </li>
          </nav>

          <ProductionTable month={month}/>
        </section>
    }

      <Modal opened={modalOpened} 
            children={<NewMonthForm canceled={onCreateNewMonthCanceled} 
                                    confirmed={onCreateNewMonthConfirmed} 
                                    product={product} />} 
            closed={onCreateNewMonthCanceled}/>

      <Modal opened={changeMonthModalOpened} 
            children={<ChangeMonthForm canceled={onChangeMonthCanceled} 
                                      confirmed={onChangeMonthConfirmed} 
                                      month={month} />} 
            closed={onChangeMonthCanceled} />
    </ErrorBoundary>
  );
}
