import React, { useState } from 'react';
import ReactDOM from 'react-dom';

import { MenuItem } from '../';

import './Menu.css';

interface Props {
  items: {
    text: string;
    clicked: (t: string) => unknown;
  }[];
  anchor: string;
}

export const Menu = (props: Props) => {
  const { items, anchor } = props;
  const [isOpened, toggleMenu] = useState(false);

  return (
    ReactDOM.createPortal(
      <nav className="menu__container">
        { items.map((item, index) => <MenuItem key={index} text={item.text} clicked={item.clicked}/>) }
      </nav>,
      document.getElementById(anchor) || document.body
    )
  );
}
