import React, { useState, useEffect, useRef } from 'react';
import ReactDOM from 'react-dom';

import { MenuItem, Button } from '../';

import './Menu.css';

interface Props {
  items: {
    text: string;
  }[];
  selected: (i: string) => unknown
}

export const Menu = (props: Props) => {
  const { items, selected } = props;
  const [isOpened, toggleMenu] = useState(false);
  const refContainer = useRef(null)

  const itemSelected = (i: string) => {
    selected(i);
    toggleMenu(false);
  }

  const onBodyClick = (e: Event) => {
    console.log(e)
  }

  useEffect(() => {
    debugger
    document.body.addEventListener('mousedown', onBodyClick);
    return document.body.removeEventListener('mousedown', onBodyClick);
  });

  return (
    <>
      <div id="menu-anchor" ref={refContainer}>
        <Button text={'Управление'} clicked={() => toggleMenu(!isOpened)}/>
      </div>

      { isOpened && ReactDOM.createPortal(
          <nav className="menu__container">
            { items.map((item, index) => <MenuItem key={index} text={item.text} clicked={itemSelected}/>) }
          </nav>,
          document.getElementById('menu-anchor') || document.body
        )
      }
    </>
  );
}
