import React, { useState, useEffect, useContext } from 'react';
import { Link, useParams } from 'react-router-dom';

import { 
  Button, 
  BtnTypes, 
  Menu, 
  MenuItem, 
  ErrorBoundary, 
  ProductionTable,
  Spinner
} from '../components';

import { DBServiceContext } from '../App';
import { WorkMonth, Products } from '../models';

import napkinsImg from '../assets/napkins.webp';
import toiletsImg from '../assets/toilets.webp';
import './ProductionPage.css';


export const ProductionPage = () => {
  const [month, setMonth] = useState({} as WorkMonth);
  const [loading, setLoading] = useState(true);
  const {product} = useParams();
  const dbService = useContext(DBServiceContext);

  useEffect(() => {
    setTimeout(() => {
      const m = dbService.getMonth();
      setMonth(m);
      setLoading(false);
    }, 1000)
  }, [])


  const itemClicked = (t: string) => {
    console.log(t)
  }

  const itemSelected = (i: string) => {
    console.log(i)
  }

  const items = [  
            {
              text: 'Новый месяц',
            },
            {
              text: 'Новая смена',
            },
            {
              text: 'Изменить план',
            },
            {
              text: 'Изменить количество смен'
            },
          ];

  const imgSrc = product === Products.napkins ? toiletsImg : napkinsImg;

	return (
    <ErrorBoundary>
    {
      loading 
      ? <Spinner imgSrc={napkinsImg}/> 
      : <section className="production-page__container">
          <nav className="navigation__container">
            <li className="navigation-item">
              <Link to="/"><Button text={'Назад'} type={BtnTypes.warn}/></Link>
            </li>
            <li className="navigation-item">
              <Menu items={items} selected={itemSelected} />
            </li>
          </nav>

          <ProductionTable month={month}/>
        </section>
    }

    </ErrorBoundary>
  );
}
