import React, { useState } from 'react';
import { Link } from 'react-router-dom';

import { Button, BtnTypes, Menu, MenuItem, ErrorBoundary } from '../components';

import './ProductionPage.css';

export const ProductionPage = () => {
  const [ menuOpened, toggleMenu ] = useState(false);
  const itemClicked = (t: string) => {
    toggleMenu(false);
  }

  const items = [  
            {
              text: 'Новый месяц',
              clicked: itemClicked
            },
            {
              text: 'Новая смена',
              clicked: itemClicked
            },
            {
              text: 'Изменить план',
              clicked: itemClicked
            },
            {
              text: 'Изменить количество смен',
              clicked: itemClicked
            },
          ];

	return (
    <ErrorBoundary>
      <section className="production-page__container">
        <nav className="navigation__container">
          <li className="navigation-item">
            <Link to="/"><Button text={'Назад'} type={BtnTypes.warn}/></Link>
          </li>
          <li className="navigation-item">
            <Button text={'Управление'} type={BtnTypes.primary} clicked={() => toggleMenu(!menuOpened)}/>
            <div id="test"></div>
          </li>
          { menuOpened && <Menu items={items} anchor={'test'}/> }
        </nav>
      </section>
    </ErrorBoundary>
  );
}
