import { WorkMonth, data } from '../models';

export class DBService {
	private workMonth = data;

	public putMonth(m: WorkMonth) {
		this.workMonth = {...m};
	}

	public getMonth(): WorkMonth {
		return this.workMonth;
	}

	public updateWorkDay(day: number, amount: number) {
		this.workMonth.workDays[day].amount = amount;
	}

	public updatePlan(plan: number) {
		this.workMonth.plan = plan;
	}

	public updatePpr(ppr: number) {
		this.workMonth.ppr = ppr;
	}
}
