import { WorkMonth, Products } from '../models';

export class DBService {
	private readonly url = 'http://localhost:4000';

	public async getWorkMonth(p: Products): Promise<WorkMonth> {
		const res = await fetch(`${this.url}/production/${p}`);

	    if (!res.ok) {
	      throw new Error(`Could not fetch ${this.url}/${p}` +
	        `, received ${res.status}`)
	    }
	    return await res.json();

	}

