import React from 'react';

import { InputField } from '../';

import './ProductionTable.css';

import { WorkMonth } from '../../models';

interface Props {
  month: WorkMonth;
}

export const ProductionTable = (props: Props) => {
  const { month } = props;
  const onWorkDayChange = (v: string) => {
    console.log(v)
  }

  const onPlanChange = (p: string) => {
    console.log(p)
  }

  const onPprChange = (p: string) => {
    console.log(p)
  }

  const onAvgChange = (p: string) => {
    console.log(p)
  }

	return (
    <table className="production-table">
      <thead className="production-table__thead">
        <tr>
          <th>План</th>
          <th>Остаток</th>
        </tr>
        <tr>
          <td><InputField type={'text'} value={String(month.plan)} changed={onPlanChange}/></td>
          <td><InputField type={'text'} value={String(month.plan)} disabled={true}/></td>
        </tr>
        <tr>
          <th>Коэффициент</th>
          <th>Среднее в смену</th>
        </tr>
        <tr>
          <td><InputField type={'text'} value={"1.1"} disabled={true}/></td>
          <td><InputField type={'text'} value={"18000"} changed={onPprChange}/></td>
        </tr>
      </thead>
      <tbody>
        <tr>
          <th>Смена</th>
          <th>Количество</th>
        </tr>
          { month.workDays.map((workDay) => {
              return (
                <tr key={workDay.day} className="production-table__tr">
                 <td className="production-table__td">
                   <InputField type={'text'} value={String(workDay.day)} disabled={true}/>
                 </td>
                 <td className="production-table__td">
                    <InputField type={'text'} value={String(workDay.amount)} changed={onWorkDayChange}/>
                 </td>
                </tr>
              )
            })
          }

      </tbody>
      <tfoot>
        <tr>
          <th>Для выполнения плана нужно делать по:</th>
          <th><InputField type={'text'} value={'11500'} disabled={true}/></th>
        </tr>
      </tfoot>
    </table>
  )
}
