import React from 'react';

import { InputField, Button, BtnTypes } from '../';
import { WorkMonth, Products, WorkDay } from '../../models';

import './ChangeMonthForm.css';

interface Props {
	confirmed: (m: WorkMonth) => unknown;
  canceled: () => unknown;
  product: Products;
}

export const ChangeMonthForm = () => {
  return (
    <form className="form">
      <section className="form__fields">
        <div className="form__field">
           <label>Количество смен</label>
          <InputField type={'text'} value={String(days)} changed={onDaysChange} />         
        </div>

        <div className="form__field">
          <label>План</label>
          <InputField type={'text'} value={String(plan)} changed={onPlanChange} />           
        </div>
      </section>


      <section className="form__controls">
        <Button text={'Отмена'} type={BtnTypes.warn} clicked={canceled}/>
        <Button text={'Создать'} type={BtnTypes.primary} clicked={onConfirmed}/>
      </section>
    </form>
  )
}
