import React, { useState, useEffect, useContext } from 'react';
import { Link, useParams } from 'react-router-dom';

import { ErrorBoundary } from '../components';
import { Button, CircularProgress } from '@material-ui/core/';

import { DBServiceContext } from '../App';
import { WorkMonth, Products } from '../models';
import './ProductionPage.css';


export const ProductionPage = () => {
  const {product} = useParams();
  const dbService = useContext(DBServiceContext);

  const [month, setMonth] = useState({
    product,
    days: 0,
    plan: 0,
    ppr: 0,
    workDays: []
  } as WorkMonth);
  const [isLoading, setIsLoading] = useState(true);
  const [menuIsOpened, setMenuIsOpened] = useState(false);
  const [changeMonthModalOpened, setChangeMonthModalOpened] = useState(false);

  useEffect(() => {
    (async () => {
      const m = await dbService.getMonth(product);
      setMonth(m);
      setIsLoading(false);
    })();
  }, [product]);

  const onMenuToggle = () => {
    setMenuIsOpened(!isLoading);
  }

  if (isLoading) {
    return (
      <div className="production-page__spinner-container">
        <CircularProgress size={"12rem"} />
      </div> 
    )
  }

  return (
    <ErrorBoundary>
      <nav>
        <Button variant="outlined" color="secondary">
          <Link to="/">Назад </Link>
        </Button>
        <Button variant="outlined" color="primary" onClick={onMenuToggle}>
          Управление
        </Button>
      </nav>
      <h2>TABLE</h2>
    </ErrorBoundary>
  );
}
