import React from 'react';

import Button from '@material-ui/core/Button';
import TextField from '@material-ui/core/TextField';
import Dialog from '@material-ui/core/Dialog';
import DialogActions from '@material-ui/core/DialogActions';
import DialogContent from '@material-ui/core/DialogContent';
import DialogContentText from '@material-ui/core/DialogContentText';
import DialogTitle from '@material-ui/core/DialogTitle';

interface Props {
	confirmed: (val: number) => void;
	canceled: () => void;
	title: string;
	cancelBtnText: string;
	confirmBtnText: string;
	label: string;
}

export const SimpleForm = (props: Props) => {
	const {confirmed, canceled, title, cancelBtnText, confirmBtnText} = props;

	const onValChange = (e: React.ChangeEvent<HTMLInputElement>) => {
		confirmed(Number(e?.target.value) || 0);
	}


	return (
		<>
			<DialogTitle>{title}</DialogTitle>
	        <DialogContent>

	          <TextField
	            margin="dense"
	            label={label}
	            type="number"
	            required
	            fullWidth
	            onChange={onValChange}
	          />
	        </DialogContent>
	        <DialogActions>
	          <Button onClick={onCanceled} color="primary">
	            Отмена
	          </Button>
	          <Button onClick={onConfirmed} color="primary" disabled={day.amount < 1}>
	            Добавить
	          </Button>
	        </DialogActions>
		</>
	)
}
