import React, { useState } from 'react';

import { InputField, Button, BtnTypes } from '../';
import { WorkMonth } from '../../models';

import './NewMonthForm.css';

interface Props {
	confirmed: () => WorkMonth;
  cancel: () => unknown;
}

export const NewMonthForm = (props: Props) => {
  const {confirmed, cancel} = props;
  const [days, setDays] = useState(0);
  const [plan, setPlan] = useState(0);

  const onDaysChange = (days: string) => {
    setDays(Number(days));
  }

  const onPlanChange = (plan: string) => {
    setPlan(Number(plan));
  }

  return (
    <section>
      <InputField type={'text'} value={String(days)} changed={onDaysChange} />
      <InputField type={'text'} value={String(plan)} changed={onPlanChange} />

      <Button text={'Отмена'} type={BtnTypes.warn}/>
      <Button text={'Создать'} type={BtnTypes.primary}/>
    </section>
  )
}
