import React, { useState } from 'react';

import { WorkMonth } from '../../models';

import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableContainer from '@material-ui/core/TableContainer';
import TableHead from '@material-ui/core/TableHead';
import TableRow from '@material-ui/core/TableRow';
import TableFooter  from '@material-ui/core/TableFooter';
import Paper from '@material-ui/core/Paper';

import './ProductionTable.css';

interface Props {
	month: WorkMonth;
}

export const ProductionTable = (props: Props) => {
	const {month} = props;
	return (
		<TableContainer component={Paper}>
	      <Table className="production-table__table" stickyHeader>

	        <TableHead>
	          <TableRow>
	            <TableCell className="production-table__cell" align="center">План</TableCell>
	            <TableCell className="production-table__cell" align="center">Остаток</TableCell>
	          </TableRow>

	          <TableRow>
	            <TableCell className="production-table__cell" align="center">{month.plan}</TableCell>
	            <TableCell className="production-table__cell" align="center">32000</TableCell>
	          </TableRow>

	          <TableRow>
	            <TableCell className="production-table__cell" align="center">Коэффициент</TableCell>
	            <TableCell className="production-table__cell" align="center">Среднее в смену</TableCell>
	          </TableRow>

	          <TableRow>
	            <TableCell className="production-table__cell" align="center">1.1</TableCell>
	            <TableCell className="production-table__cell" align="center">2400</TableCell>
	          </TableRow>

	          <TableRow>
	            <TableCell className="production-table__cell" align="center">Часы ППР</TableCell>
	            <TableCell className="production-table__cell" align="center">{month.ppr}</TableCell>
	          </TableRow>

	          <TableRow>
	            <TableCell className="production-table__cell" align="center">Среднее с ППР</TableCell>
	            <TableCell className="production-table__cell" align="center">2480</TableCell>
	          </TableRow>

	          <TableRow>
	            <TableCell className="production-table__cell" align="center">Смена</TableCell>
	            <TableCell className="production-table__cell" align="center">Количество</TableCell>
	          </TableRow>

	        </TableHead>

	        <TableBody>
	          {month.workDays.map((workDay) => (
	            <TableRow key={workDay.day}>
	              <TableCell className="production-table__cell" align="center">
	                {workDay.day}
	              </TableCell>
	              <TableCell className="production-table__cell" align="center">{workDay.amount}</TableCell>
	            </TableRow>
	          ))}
	        </TableBody>

	        <TableFooter>
	          <TableRow>
	            <TableCell className="production-table__cell" variant="head" align="center">Для выполнения плана нужно делать по:</TableCell>
	            <TableCell className="production-table__cell" variant="head" align="center">2520</TableCell>
	          </TableRow>
	        </TableFooter>
	      </Table>
	    </TableContainer>
	)
}


