import React from 'react';
import ReactDOM from 'react-dom';

import './Modal.css';

interface Props {
	children: JSX.Element;
	opened: boolean;
}

export const Modal = (props: Props) => {
	const {children, opened} = props;

  if (opened) {
    return (ReactDOM.createPortal(
              <section className="modal" data-theme="light">
                {children}
              </section>,
              document.body))
  }

  return null;
}
