import './InputField.css';

interface Props {
  type: 'text' | 'number' | 'email';
  value: string;
  required?: boolean;
  disabled?: boolean;
  readonly?: boolean;
  changed?: (v: string) => unknown;
}

export const InputField = (props: Props) => {
  const { type, value, required, changed, readonly } = props;
  const inputEl = useRef<HTMLInputElement>(null);
  const valChanged = () => {
    console.dir(inputEl?.current?.value)
    if (changed)
      changed(inputEl?.current?.value || '');
  }

	return <input className="input"
                type={type} 
                value={value} 
                required={required}
                readonly={readonly}
                onChange={valChanged}
                ref={inputEl}
                data-theme="dark"/>
}
