import React, { useState, useEffect, useContext } from 'react';
import { Link, useParams } from 'react-router-dom';

import { ErrorBoundary } from '../components';
import { Button, CircularProgress } from '@material-ui/core/';

import { DBServiceContext } from '../App';
import { WorkMonth, Products } from '../models';
import './ProductionPage.css';


export const ProductionPage = () => {
  const {product} = useParams();
  const dbService = useContext(DBServiceContext);

  const [month, setMonth] = useState({
    product,
    days: 0,
    plan: 0,
    ppr: 0,
    workDays: []
  } as WorkMonth);
  const [isLoading, setIsLoading] = useState(true);
  const [modalOpened, setModalOpened] = useState(false);
  const [changeMonthModalOpened, setChangeMonthModalOpened] = useState(false);

  useEffect(() => {
    (async () => {
      const m = await dbService.getMonth(product);
      setMonth(m);
      setIsLoading(false);
    })();
  }, [product]);

  const onModalToggle = () => {
    setModalOpened(!modalOpened);
  }

  const onCreateNewMonthCanceled = () => {
    setModalOpened(false);
  }

  const onChangeMonthModalToggle = () => {
    setChangeMonthModalOpened(!changeMonthModalOpened);
  }

  const onChangeMonthCanceled = () => {
    setChangeMonthModalOpened(false);
  }

  if (isLoading) {
    return (
      <div className="production-page__spinner-container">
        <CircularProgress size={"12rem"} />
      </div> 
    )
  }

  return (
    <ErrorBoundary>
      <h2>TABLE</h2>
    </ErrorBoundary>
  );
}
